import type { Express, Request, Response } from "express";
import { storage } from "../storage";
import { insertUserSchema } from "@shared/schema";
import { z } from "zod";
import bcrypt from "bcryptjs";

const loginSchema = z.object({
  email: z.string().email(),
  password: z.string().min(1),
});

const registerSchema = insertUserSchema.extend({
  password: z.string().min(6),
});

export function registerAuthRoutes(app: Express) {
  // POST /api/auth/login - User login
  app.post("/api/auth/login", async (req: Request, res: Response) => {
    try {
      const { email, password } = loginSchema.parse(req.body);

      const user = await storage.getUserByEmail(email);
      if (!user || !user.isActive) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const validPassword = await bcrypt.compare(password, user.password);
      if (!validPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // In a real app, you'd create a JWT token or session here
      const { password: _, ...userWithoutPassword } = user;
      res.json({ 
        data: userWithoutPassword,
        message: "Login successful" 
      });
    } catch (error) {
      console.error("Error during login:", error);
      res.status(400).json({ 
        message: error instanceof z.ZodError ? "Invalid login data" : "Login failed",
        errors: error instanceof z.ZodError ? error.errors : undefined,
      });
    }
  });

  // POST /api/auth/register - User registration
  app.post("/api/auth/register", async (req: Request, res: Response) => {
    try {
      const userData = registerSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(userData.email);
      if (existingUser) {
        return res.status(409).json({ message: "User already exists" });
      }

      const existingUsername = await storage.getUserByUsername(userData.username);
      if (existingUsername) {
        return res.status(409).json({ message: "Username already taken" });
      }

      // Hash password
      const hashedPassword = await bcrypt.hash(userData.password, 12);

      const user = await storage.createUser({
        ...userData,
        password: hashedPassword,
      });

      const { password: _, ...userWithoutPassword } = user;
      res.status(201).json({ 
        data: userWithoutPassword,
        message: "User created successfully" 
      });
    } catch (error) {
      console.error("Error during registration:", error);
      res.status(400).json({ 
        message: error instanceof z.ZodError ? "Invalid registration data" : "Registration failed",
        errors: error instanceof z.ZodError ? error.errors : undefined,
      });
    }
  });

  // GET /api/auth/me - Get current user (placeholder for session-based auth)
  app.get("/api/auth/me", async (req: Request, res: Response) => {
    // In a real app, you'd extract user ID from JWT token or session
    res.status(401).json({ message: "Not implemented - requires session management" });
  });

  // POST /api/auth/logout - User logout
  app.post("/api/auth/logout", async (req: Request, res: Response) => {
    // In a real app, you'd invalidate the JWT token or destroy session
    res.json({ message: "Logged out successfully" });
  });
}

