// Re-export types from shared schema for frontend use
export type {
  User,
  InsertUser,
  Company,
  InsertCompany,
  Contact,
  InsertContact,
  Address,
  InsertAddress,
  Email,
  InsertEmail,
  Phone,
  InsertPhone,
  FieldDefinition,
  InsertFieldDefinition,
  Job,
  InsertJob,
  JobApplication,
  InsertJobApplication,
  Interview,
  InsertInterview,
  AnalyticsEvent,
  InsertAnalyticsEvent,
  ReportConfiguration,
  InsertReportConfiguration,
  Dashboard,
  InsertDashboard,
} from "@shared/schema";

// Additional frontend-specific types
export interface ApiResponse<T> {
  data: T;
  meta?: {
    count: number;
    total: number;
    hasNext: boolean;
  };
  links?: {
    next: string | null;
  };
}

export interface SearchParams {
  q?: string;
  sort?: string;
  limit?: number;
  cursor?: string;
  includeDeleted?: boolean;
}

