  industry?: string;
  companyType?: string;
  employeeCountRange?: string;
  recordStatus?: string;
  country?: string;
  include?: string;
}

export interface ContactSearchParams extends SearchParams {
  companyId?: string;
  seniority?: string;
  lifecycleStage?: string;
  recordStatus?: string;
  country?: string;
  include?: string;
}

export interface Country {
  code: string;
  name: string;
  flag: string;
}

export interface FormFieldDefinition {
  key: string;
  label: string;
  type: 'text' | 'number' | 'boolean' | 'date' | 'select' | 'multi_select' | 'email' | 'url' | 'phone';
  options?: string[];
  isRequired?: boolean;
}

